import React, {Component,Fragment} from 'react';
import {Button, Container, Nav, Navbar} from "react-bootstrap";
import '../../asset/css/TopNavCss.css';
import '../../asset/css/style.css';
import {Link} from "react-router-dom";

class TopNavigation extends Component {

    constructor(props) {
        super();
        this.state={
            author_username:sessionStorage.getItem("UserName"),
            navTitle:"navTitle",
            navBackground:"navBackground",
            navItem:"navBarItem",
            pageTitle: props.pageTitle,
            LoginBtn:"VisibilityBtn",
            ProfileBtn:"HiddenBtn",
            LogoutBtn:"HiddenBtn",

        }
    }

    onLogoutBtn=()=>{
        sessionStorage.removeItem("UserName");
    }

    onShowBtn=()=>{
        if (this.state.author_username===null){
            this.setState({
                LoginBtn:"VisibilityBtn",
                ProfileBtn:"HiddenBtn",
                LogoutBtn:"HiddenBtn",
            })
        }else {
            this.setState({
                LoginBtn:"HiddenBtn",
                ProfileBtn:"VisibilityBtn",
                LogoutBtn:"VisibilityBtn",
            })
        }
    }

    onScroll=()=>{
        if (window.scrollY>100){
            this.setState({
                navTitle:"navTitleScroll",
                navBackground:"navBackgroundScroll",
                navItem:"navBarItemScroll"
            })
        }
        else if (window.scrollY<100){
            this.setState({
                navTitle:"navTitle",
                navBackground:"navBackground",
                navItem:"navBarItem"
            })
        }
    }

    componentDidMount() {
        this.onShowBtn();
        window.addEventListener('scroll',this.onScroll);
    }

    render() {
        return (
            <Fragment>

                    <title>{this.state.pageTitle}</title>
                    <Navbar fixed={"top"} className={this.state.navBackground} expand="lg" bg="dark" variant="dark">
                        <Container>
                            <Navbar.Brand><Link className={this.state.navTitle} to="/">React Blog App</Link></Navbar.Brand>
                            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                            <Navbar.Collapse id="responsive-navbar-nav">
                                <Nav className="me-auto">
                                </Nav>
                                <Nav>
                                    <Nav.Link className={this.state.navItem} href="#deets">More debts</Nav.Link>
                                    <Nav.Link className={this.state.navItem} href="#deets">More debts</Nav.Link>
                                    <Nav.Link className={this.state.navItem} href="#deets">More debts</Nav.Link>
                                    <Nav.Link className={this.state.navItem} href="#deets">More debts</Nav.Link>
                                    <Nav.Link className={this.state.navItem} to="">More debts</Nav.Link>
                                    <Nav.Link className={this.state.navItem} href="">More debts</Nav.Link>
                                    <Button className={this.state.LoginBtn} variant="outline-danger"><Link to="/userlogin">Login</Link></Button>
                                    <Button className={this.state.ProfileBtn} variant="outline-danger"><Link to={"/userProfile/"+this.state.author_username}>Profile</Link></Button>
                                    <Button onClick={this.onLogoutBtn} className={this.state.LogoutBtn} variant="outline-danger"><Link to="/">Logout</Link></Button>
                                </Nav>
                            </Navbar.Collapse>
                        </Container>
                    </Navbar>
            </Fragment>
        );
    }
}

export default TopNavigation;
