import React, {Component,Fragment} from 'react';
import {Button, Container, Nav, Navbar} from "react-bootstrap";
import '../../asset/css/TopNavCss.css';
import '../../asset/css/style.css';
import {Link, NavLink} from "react-router-dom";

class TopNavigation extends Component {
    constructor(props) {
        super();
        this.state={
            author_username:sessionStorage.getItem("UserName"),
            navTitle:"navTitle",
            navBackground:"navBackground",
            navItem:"navBarItem",
            pageTitle: props.pageTitle,

        }
    }

    onLogoutBtn=()=>{
        sessionStorage.removeItem("UserName");
    }

    onScroll=()=>{
        if (window.scrollY>100){
            this.setState({
                navTitle:"navTitleScroll",
                navBackground:"navBackgroundScroll",
                navItem:"navBarItemScroll"
            })
        }
        else if (window.scrollY<100){
            this.setState({
                navTitle:"navTitle",
                navBackground:"navBackground",
                navItem:"navBarItem"
            })
        }
    }

    componentDidMount() {
        window.addEventListener('scroll',this.onScroll);
    }

    render() {
        return (
            <Fragment>

                    <title>{this.state.pageTitle}</title>
                    <Navbar fixed={"top"} className={this.state.navBackground} expand="lg" bg="dark" variant="dark">
                        <Container>
                            <Navbar.Brand><Link className={this.state.navTitle} to="/">React Blog App</Link></Navbar.Brand>
                            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                            <Navbar.Collapse id="responsive-navbar-nav">
                                <Nav className="me-auto">
                                </Nav>
                                <Nav>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/food">Food</Link></Nav.Link>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/project">Project</Link></Nav.Link>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/news">News</Link></Nav.Link>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/song">Song</Link></Nav.Link>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/movie">Movie</Link></Nav.Link>
                                    <Nav.Link><Link className={this.state.navItem} to="/category/play">Play</Link></Nav.Link>

                                    <Button className="m-1" variant="outline-danger"><Link to={"/userProfile/"+this.state.author_username}>Profile</Link></Button>
                                    <Button className="m-1" onClick={this.onLogoutBtn} variant="outline-danger"><a href="/">Logout</a></Button>
                                </Nav>
                            </Navbar.Collapse>
                        </Container>
                    </Navbar>
            </Fragment>
        );
    }
}

export default TopNavigation;
