import React, {Component,Fragment} from 'react';
import TopNavigation from "../components/TopNavigation/TopNavigation";
import UserLogin from "../components/UserLogin/UserLogin";
import '../asset/css/style.css';
import TopNavigationLogin from "../components/TopNavigation/TopNavigationLogin";

class UserLoginPage extends Component {

    constructor() {
        super();
        this.state={
            NavigationLogin:"",
            Navigation:"navBtn",
        }
    }


    onChangNavigation=()=>{
        if (sessionStorage.getItem("UserName")!==null){
            this.setState({
                NavigationLogin:"navBtn",
                Navigation:"",
            })
        }else {
            this.setState({
                NavigationLogin:"",
                Navigation:"navBtn",
            })
        }
    }


    componentDidMount() {
        window.scroll(0,0);
        this.onChangNavigation();
    }


    render() {
        return (
            <Fragment>

                <div className={this.state.NavigationLogin}>
                    <TopNavigationLogin pageTitle ="Login Page"/>
                </div>
                <div className={this.state.Navigation}>
                    <TopNavigation pageTitle ="Login Page"/>
                </div>

                <UserLogin/>
            </Fragment>
        );
    }
}

export default UserLoginPage;
